<style>
    .main-logo-wrapper {
        display: grid;
        width: 100%;
        height: max-content;
        grid-template-columns: 40% 60%;
        justify-items: last baseline;
    }

    .main-logo-swallow {
        display: flex;
        grid-column: 1 / span 1;
        width: 100%;
        place-items: center;
    }

    .logo-lexicography {
        grid-column: 2 / span 1;
    }

    .logo-lexicography h1 {
        display: flex;
        flex-direction: column;
        row-gap: var(--spacing-1);
        height: 100%;
        justify-content: center;
        font-weight: 300;
        align-items: flex-start;
        color: var(--dark);
        line-height: .9;
    }

    .logo-lexicography h1 span {
        display: block;
    }

    .logo-lexicography h1 span.top-text {
        font-size: var(--font-size-h1);
    }

    .logo-lexicography h1 span.middle-text {
        font-size: calc(var(--font-size-h1) * 1.6875);
    }

    .logo-lexicography h1 span.bottom-text {
        font-family: var(--font-read);
        font-size: var(--font-size-h4);
        padding: 0 0 0 var(--spacing-2);
        color: var(--shade-9);
    }

</style>